import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../features/auth/authActions";

const Navbar = () => {
  const dispatch = useDispatch();
  const isAuthenticated = useSelector((state) => state.auth.isAuthenticated);

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <nav className="bg-blue-600 p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-white text-lg font-bold">
          E-Learning
        </Link>
        <div>
          {isAuthenticated ? (
            <>
              <Link to="/classes" className="text-white mx-2">
                Classes
              </Link>
              <Link to="/files" className="text-white mx-2">
                Files
              </Link>
              <Link to="/withdraw" className="text-white mx-2">
                Withdraw
              </Link>
              <button onClick={handleLogout} className="text-white mx-2">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/register" className="text-white mx-2">
                Register
              </Link>
              <Link to="/login" className="text-white mx-2">
                Login
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
